import React from "react";

class Detail extends React.Component{
    componentDidMount(){
        const{location,history} = this.props;
        if (location.state === undefined){
            history.push('/');

        }
    }
    render(){
        const{location}=this.props;
        if (location.state){
            return (
        <div>
        <img src={location.poster} alt={location.title} title={location.title}/>
        <div className="movie_data">

        <h3 className="movie_title">{location.title}</h3>
        <h5 className="movie_year">{location.year}</h5>

        <ul className="movie_genres">
            {location.genres.map((genre,index)=>(
                <li key={index} className="genres_genre">{genre}</li>
            ))}
        </ul>
        <p className="movie_summary">{location.summary.slice(0,180)}...</p>
        </div>
        </div>
            );
        }else{
            return null;
        }
        }   
}

export default Detail;
